mapper.select:
select id, (select nickname from tc_user u where is_inn=0 u.user_id=e.user_id) as user_id, (select real_name from tc_user u where u.user_id=e.user_id) as expert_name, (select type_value from tb_expert_type t where t.id=e.top_title) as top_title, city_code, expert_discription_short, expert_portrait_url,expert_wechat_number, expert_experience, expert_tel_number, motto, expert_display_pic_list, expert_discription_detail, delete_flag, insert_time, update_time from tb_expert_top e
city_code=#{cityCode,jdbcType=VARCHAR}
e.expert_name like '%${expertName}%'
and e.top_title=#{topType,jdbcType=VARCHAR}
and delete_flag=#{deleteFlag,jdbcType=VARCHAR}
order by e.insert_time desc limit #{beginResult},#{pageSize}

select count(1) from tb_expert_top

	is_inn=0 and city_code=#{cityCode,jdbcType=VARCHAR}
	
		expert_name like '%${expertName}%'
	
	
		and top_title=#{topType,jdbcType=VARCHAR}
	
	
		and delete_flag=#{deleteFlag,jdbcType=VARCHAR}

		select count(1) from tb_expert_top
		where is_inn=0 user_id = #{userId,jdbcType=VARCHAR}
		
  			and id != #{expertId}
