mapper.select:
select id, (select nickname from tc_user u where u.user_id=e.user_id) as user_id, (select real_name from tc_user u where u.user_id=e.user_id) as expert_name, (select title from inn_goods_category t where t.id=e.top_title) as category_title, city_code, expert_discription_short, expert_portrait_url,expert_wechat_number, expert_experience, expert_tel_number, motto, expert_display_pic_list, expert_discription_detail, delete_flag, insert_time, update_time from tb_expert_top e
is_inn=1 and e.city_code in(select id from tb_city C where C.city_code=#{cityCode,jdbcType=VARCHAR})
and e.expert_name like '%${expertName}%'
and e.top_title=#{category,jdbcType=VARCHAR}
order by e.insert_time desc limit #{beginResult},#{pageSize}

		select count(1) from tb_expert_top
		where user_id = #{userId,jdbcType=VARCHAR}
		and is_inn=0 
		
  			and id != #{expertId}
