mapper.select:
select G.*,(select cn_name from inn_goods_brand where G.brand_id=id) brandName
from inn_goods G

	
	
		and brand_id in (select id from inn_goods_brand where category_id=#{categoryId})
	
	
		and brand_id =#{brandId}
	
	
		and goods_title like '%${goodsTitle}%'
	
	
		and DATE_FORMAT(up_time,'%Y-%m-%d')>=#{beginTime}
	
	
		and DATE_FORMAT(up_time,'%Y-%m-%d')<=#{endTime}
	

order by up_time desc limit #{beginResult},#{pageSize}

select count(1) from inn_goods

	
	
		and brand_id in (select id from inn_goods_brand where category_id=#{categoryId})
	
	
		and brand_id =#{brandId}
	
	
		and goods_title like '%${goodsTitle}%'
	
	
		and DATE_FORMAT(up_time,'%Y-%m-%d')>=#{beginTime}
	
	
		and DATE_FORMAT(up_time,'%Y-%m-%d')<=#{endTime}

select id, brand_id, sku_code,goods_title from inn_goods 

	status!=1
	and city_id=#{cityId}
	
		and brand_id=#{brandId}

mapper.update:
update inn_goods G
brand_id=#{brandId,jdbcType=VARCHAR}, city_id= #{cityId,jdbcType=CHAR}, score=#{score,jdbcType=VARCHAR}, prime_price=#{primePrice,jdbcType=DECIMAL}, promotion_price=#{promotionPrice,jdbcType=DECIMAL}, cost_price=#{costPrice,jdbcType=DECIMAL}, product_area=#{productArea,jdbcType=VARCHAR}, goods_title=#{goodsTitle,jdbcType=VARCHAR}, goods_synopsis=#{goodsSynopsis,jdbcType=VARCHAR}, goods_description=#{goodsDescription,jdbcType=VARCHAR}, search_tag=#{searchTag,jdbcType=VARCHAR}, stock=#{stock,jdbcType=INTEGER}, small_picture=#{smallPicture,jdbcType=VARCHAR}, big_picture=#{bigPicture,jdbcType=VARCHAR}, goods_detail=#{goodsDetail,jdbcType=VARCHAR}, sold_number=#{soldNumber,jdbcType=INTEGER}, is_hot=#{isHot,jdbcType=CHAR}, is_new=#{isNew,jdbcType=CHAR}, G.status=#{status,jdbcType=INTEGER},
down_time=now(),
down_time=NULL,
weight=#{weight,jdbcType=DOUBLE}
where id=#{id,jdbcType=VARCHAR}

		update inn_goods set status =1,down_time=now()
		where id in
		
            #{id,jdbcType=VARCHAR}

mapper.select:
	select count(1) from inn_goods G
	
		city_id=#{cityId,jdbcType=VARCHAR}
		and brand_id=#{brandId,jdbcType=VARCHAR}
		and (select group_concat(spec_item_id order by id separator ",") from inn_spec_item_goods_relation R where R.goods_id=G.id)
			=#{itemIds,jdbcType=VARCHAR}
  	
  		and id !=#{goodsId,jdbcType=VARCHAR}
